! function (e) {
	"use strict";
  var a, r = {}, d = [], color = [];

  //https://stackoverflow.com/questions/19491336/how-to-get-url-parameter-using-jquery-or-plain-javascript

// This Week Chart page
let month_chart = $("#chart_monthid").val();
let stdchart_id = $('#profile_username').attr('data-id');
$.ajax({
  url: '/chart_monthdata',
  method: 'POST',
  data: {send_chartid:month_chart, send_id:stdchart_id},
  dataType: 'json',
  async: false,
  success:function(data){
    let count = data.array.length;
    console.log(count);
    let calue;
      if(count == '0'){
        $("#month_result").delay(5).fadeIn('slow').delay(1500).fadeOut('slow');
      }
      else{
        for(let i=0; i<count; i++){
            calue = parseFloat(data.array[i].val/data.sum *100).toFixed(2);
            d.push({value: calue, label: data.array[i].name});
            color.push(getColor());
          }
      }
    }
});

  initChart_Month();

  function initChart_Month() {
    r = Morris.Donut({
      element: "donut-morris-chart-month",
      data: d,
      colors: color,
      resize: !0,
      formatter: function (e) {
        return e + "%"
      }
    })
  }
  
  function getColor() {
    const randomColor = Math.floor(Math.random()*16777215).toString(16);
    if(randomColor == "#ddd") {return;}
    else{
      document.body.style.backgroundColor = "#" + randomColor;
      return "#" + randomColor;
    }
  }

$('#chart_monthid').change(function(){
    $("#donut-morris-chart-month").empty();
    let chart_sbj=$(this).find(':selected').data('id');
    let stdchart_id = $('#profile_username').attr('data-id');
    $.ajax({
      url: '/month_selectdata',
      method: 'POST',
      data: {send_selectid:chart_sbj, send_selid:stdchart_id},
      dataType: 'json',
      async: false,
      success:function(data){
        console.log(data.array.length);
        let count = data.array.length;
        let calue; d = []; color = [];

        if(count == '0'){
          $("#month_result").delay(5).fadeIn('slow').delay(1500).fadeOut('slow');
        }
        else{
        for(let i=0; i<count; i++){
            calue = parseFloat(data.array[i].val/data.sum *100).toFixed(2);
            d.push({value: calue, label: data.array[i].name});
            color.push(getColor());
          }
        }
        initChart_Month();

      }
    });
  });
}(jQuery);

